/*
 * Url preview script 
 * powered by jQuery (http://www.jquery.com)
 * written by Alen Grakalic (http://cssglobe.com)
 * for more info visit http://cssglobe.com/post/1695/easiest-tooltip-and-image-preview-using-jquery
 */
this.imageTooltip = function(){	
	xOffset = 10;
	yOffset = 30;
	// these 2 variable determine popup's distance from the cursor
	// you might want to adjust to get the right result
	$(".imageTooltip").hover(function(e){
		image_src = $(this).children('input.imageTooltip').val();
		$("body").append("<p id='imageTooltip'><img src='"+ image_src +"' alt='url preview' /></p>");								 
		$("#imageTooltip")
			.css("top",(e.pageY - xOffset) + "px")
			.css("left",(e.pageX + yOffset) + "px")
			.fadeIn(100);						
    },
	function(){
		$("#imageTooltip").remove();
    });	
	$(".imageTooltip").mousemove(function(e){
		$("#imageTooltip")
			.css("top",(e.pageY - xOffset) + "px")
			.css("left",(e.pageX + yOffset) + "px");
	});			
};
this.tooltip = function(){
	xOffset = 10;
	yOffset = 30;
	// these 2 variable determine popup's distance from the cursor
	// you might want to adjust to get the right result
	$(".tooltip").hover(function(e){
		text = '';
		if($(this).hasClass('gameTooltip'))
		{
			text += '<b class="mediumText">' + $(this).children('input.gameTooltip').val() + '</b><br>';
		}
		text += $(this).children('input.tooltip').val();
		$("body").append("<p id='tooltip'>"+ text +"</p>");
		$("#tooltip")
			.css("top",(e.pageY - xOffset) + "px")
			.css("left",(e.pageX + yOffset) + "px")
			.fadeIn(100);
    },
	function(){
		$("#tooltip").remove();
    });	
	$(".tooltip").mousemove(function(e){
		$("#tooltip")
			.css("top",(e.pageY - xOffset) + "px")
			.css("left",(e.pageX + yOffset) + "px");
	});			
};

