function formHelpers() {
	$('.submitForm').unbind('click').click(function() {
		$(this).parents('form').submit();
		//$(this).parents('form').find('input[type="submit"]').click();
	});
	$('.noSubmit').submit(function(e) {
		e.preventDefault();
	});
}

