function flashTopGamesPromo() {
	var list = $('#topGamesPromo');
	if(!$(list).is('ol'))
	{
		return;
	}
	elements = $('#topGamesPromo').find('li');
	if(!$(list).children('.elementAnimated').is('input')) {
		$(list).append('<input type="hidden" class="elementAnimated" value="0">');
	}
	var topGamesItem = parseInt($(list).children('.elementAnimated').val());
	
	if(topGamesItem > $(elements).length) {
		$(list).children('.elementAnimated').remove();
		setTimeout(flashTopGamesPromo, 8000);
	} else {
		setTimeout(flashTopGamesPromo, 100);
	}
	
	$(elements[topGamesItem]).animate({ 
	        opacity: 0.1
		}, 100).animate({
			opacity: 1
		}, 100, function() {
			if(this.style != null)
			this.style.filter = '';
		});
	
	$(list).children('.elementAnimated').val(topGamesItem+1);
}


function latestWinnersRemoveFirst(){
	first_element = $('#latestWinners div').filter(':eq(0)');
	$(first_element).children('div').animate({height: 0}, 400).parent().animate({opacity: 0}, 410, 'swing', function() {
		latestWinnerAddLast(first_element);
	});
	time = Math.floor ( Math.random ( ) * 2000 + 1 ) + 1500;
	setTimeout(latestWinnersRemoveFirst, time);
}
function latestWinnerAddLast(first){
	$(first).css('opacity', null).children('div:not(.clr)').css('height', null);
	$('#latestWinners').append(first);
}




function latestWinnersRemoveFirst2(){
	first_element = $('.latestWinners2 .winner').filter(':eq(0)');
	$(first_element).children('div').animate({height: 0}, 400).parent().animate({opacity: 0, height:0}, 410, 'swing', function() {
		first_element.css({'visible' : 'hidden'})
		latestWinnerAddLast2(first_element);
	});
	time = Math.floor ( Math.random ( ) * 2000 + 1 ) + 1500;
	setTimeout(latestWinnersRemoveFirst2, time);
}
function latestWinnerAddLast2(first){
	$(first).css('opacity', null).children('div:not(.clr)').css('height', null).parent().css({'height' : '36px'});
	$('.latestWinners2').append(first);
}







function puls5() {
	$('.puls5').animate({opacity: 0.3}, 100, function()	{
		$(this).animate({opacity: 1}, 100);
	});
}

function initSupportPromoSticky() {
	var supportPromo = $('#supportPromo').parents('li');
	var supportPromoHeight = $(supportPromo).height();
	var contentOffset = $('#content').offset().top + $('#content').height();
	var supportPromoStartOffset = parseInt($(supportPromo).offset().top);
	$(window).scroll(function() {
		margin = $(window).scrollTop() - supportPromoStartOffset - supportPromoHeight + 10;
		margin = margin > 0 ? margin : 0;
		if(contentOffset > $(window).scrollTop() + $(supportPromo).height())
		{
			$(supportPromo)
	        	.stop()
	        	.animate({"marginTop": (margin > 0 ? margin + 20 : 0) + "px"}, "slow" );
		}
	});
}

function styleGameTypeMenuSearch()
{
	var search_element = $('#searchTop').find('#id_search_game');
	if($(search_element).val() == '')
	{
		$(search_element).css({'font-size':'10px','padding-top':'4px','padding-bottom':'4px','color':'#d4d4d4'}).val($('#search_translated').val());
	}
	$(search_element).focus(function(){removeGameTypeMenuSearchStyling(this)});
	
	$(search_element).parents('form').submit(function()
	{
		removeGameTypeMenuSearchStyling(search_element);
	});
}
function removeGameTypeMenuSearchStyling(element)
{
	var search_default_value;
	
	if($(element).attr('id') == 'id_search_game')
	{
		signin_default_value = $('#search_translated').val();
	}
	if(!$(element).hasClass('hadFocus') && ($(element).val() == search_default_value || $(element).val() == ''))
	{
		$(element).css({'font-size':'14px','padding-top':'2px','padding-bottom':'2px','color':'#000000'}).val('').addClass('hadFocus');
	}
}

