function staticToolTip(){
	var isOver = false;
	var timeOut;
	var current;
	
	$('.staticToolTip').mouseenter(function(){
			
		hdr = $(this).children('input.staticTooltipHeader').val();
		bdy = $(this).children('input.staticTooltipBody').val();
		$('.staticToolTipContainer').remove();
		clearTimeout(timeOut);
		
		if($(this).find('img').length){
			offSet = $(this).find('img').offset();
		}else{
			offSet = $(this).offset();
		}
		
		current = this;
		
		$('body').append("<div class='staticToolTipContainer'>" + 
				"<div class='staticToolTip_header'><h2>" + hdr + "</h2></div>" + 
				"<div class='staticToolTip_body'><p style='color:#333;'>" + bdy + "</p></div>" +  
				"<div class='staticToolTip_footer'></div>" +
				"<div class='clr'></div>" + 
				"</div>");
		
		if($(this).find('img').length){
			$(this).parent().find('.staticToolTip_social span:first').clone().appendTo($('.staticToolTip_footer'));
			$(this).parent().find('.staticToolTip_social span:last').clone().prependTo($('.staticToolTip_body'));
		}else{
			$(current).find('.staticToolTip_social span:first').clone().appendTo($('.staticToolTip_footer'));
			$(current).find('.staticToolTip_social span:last').clone().prependTo($('.staticToolTip_body'));
		}
		
		//isOver = true;
		
		addListener($('.staticToolTipContainer:first'));
		
		timeOut = setTimeout(function(){
			if($(current).find('img').length){
				$('.staticToolTipContainer')
					.css('top',(offSet.top + ($(current).find('img').attr('height') / 2) - 20) + 'px')
					.css('left',(offSet.left + $(current).find('img').attr('width') - 20) + 'px');
			}else{
				$('.staticToolTipContainer')
					.css('top',(offSet.top - 20) + 'px')
					.css('left',(offSet.left + $(current).width() + 5) + 'px');
			}
		}, 500);

	
		
    }).mouseleave(function(){
    	clearTimeout(timeOut);
    	timeOut = setTimeout(function(){
			$('.staticToolTipContainer').remove();
		}, 100);
    });
	
	function addListener(target){
		$(target).hover(function(){
			isOver = true;
			clearTimeout(timeOut);
	    }).mouseleave(function(){
	    	isOver = false;
	    	$(this).remove();
		});
	}
}

