from django.contrib import adminfrom mysite.helps.models import Helpclass HelpAdmin(admin.ModelAdmin):    list_filter = ['channel']    def queryset(self, request):        qs = super(HelpAdmin, self).queryset(request)        if request.user.is_superuser:            return qs        return qs.filter(author=request.user)    def save_model(self, request, obj, form, change):        if not obj.id:            obj.author = request.user        obj.save()admin.site.register(Help, HelpAdmin)