from django import formsfrom django.contrib import adminfrom helps.models import Helpfrom tinymce.widgets import TinyMCEclass HelpAdminForm(forms.ModelForm):    content = forms.CharField(widget=TinyMCE(attrs={'theme':'advanced', 'cols': 80, 'rows': 30}))    class Meta:        model = Helpclass HelpAdmin(admin.ModelAdmin):    list_filter = ['channel']    form = HelpAdminForm    def queryset(self, request):        qs = super(HelpAdmin, self).queryset(request)        if request.user.is_superuser:            return qs        return qs.filter(author=request.user)    def save_model(self, request, obj, form, change):        if not obj.id:            obj.author = request.user        obj.save()admin.site.register(Help, HelpAdmin)