from django.contrib import adminfrom mysite.helps.models import Helpfrom django.http import HttpRequestimport datetimeclass HelpAdmin(admin.ModelAdmin):    def queryset(self, request):        qs = super(HelpAdmin, self).queryset(request)        if request.user.is_superuser:            return qs                return qs.filter(author=request.user)#    pass    def save_model(self, request, obj, form, change):        if not obj.id:            obj.author = request.user            obj.created = datetime.datetime.now()        obj.save()admin.site.register(Help, HelpAdmin)