# -*- coding: utf-8 -*-from mysite.lotteries.models import *from django.contrib import adminfrom django import formsclass LotteryAdminForm(forms.ModelForm):    week_cycle = forms.MultipleChoiceField([(1, '一'), (2, '二'), (3, '三'), (4, '四'), (5, '五'), (6, '六'), (7, '日')])    class Meta:        model = Lottery#        exclude = ['week_cycle', ]#    passclass LotteryAdmin(admin.ModelAdmin):    list_display = ('title', 'code')#    fieldsets = [#        ('General', {'fields': ['title', 'code', 'description', 'lotterytype', 'channel', 'sort']}),#        ('Date information', {'fields': ['week_cycle', 'yearly_break_start', 'yearly_break_end']}),#        ('Settings', {'fields': ['issue_set', 'number_rule']}),#        ('Money Setting', {'fields': ['min_commission_gap', 'min_profit']}),#    ]#    list_filter = ['created_at']    search_fields = ['title']    form = LotteryAdminForm##class LotteryTypeAdmin(admin.ModelAdmin):#    list_display = ('title')#    search_fields = ['title']class MethodAdmin(admin.ModelAdmin):    list_display = ('title', 'lottery')    fieldsets = [        ('General', {'fields': ['title', 'lottery', 'description', 'is_closed']}),        ('Prize', {'fields': ['function_name']}),        ('Lock', {'fields': ['is_use_lock', 'init_lock_func', 'lock_table_name']}),        ('Settings', {'fields': ['no_count', 'level_count']}),        ('Money Setting', {'fields': ['max_lost', 'total_price']}),    ]    admin.site.register(Lottery, LotteryAdmin)admin.site.register(LotteryType)admin.site.register(Method, MethodAdmin)admin.site.register(Issue)