from django.contrib import adminfrom mysite.notices.models import Noticeclass NoticeAdmin(admin.ModelAdmin):    def queryset(self, request):        qs = super(NoticeAdmin, self).queryset(request)        if request.user.is_superuser:            return qs        return qs.filter(author=request.user)#    pass    def save_model(self, request, obj, form, change):        if not obj.id:            obj.author = request.user        obj.checker = request.user        obj.save()admin.site.register(Notice, NoticeAdmin)